
public class widening {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i = 34;
        long l  = i *3;
        double d = (5*l);
        System.out.println("Integer value:" +i);
        System.out.println("Long  value:" +l);
        System.out.println("double value:" +d);
	}

}
